        phone1,
        password,
        // img,
      };
      let res = "";
      if (id) {
        res = await axios.put("http://localhost:1122/rfa/user/" + id, data);
      }
      alert(res.data);
      console.log(res.data);
      window.location.reload();
    } catch (error) {}

    //    else if (role_id == 2) {
    //     try {
    //       const data = {
    //         seller_name,
    //         seller_email,
    //         phone1,
    //         password,
