                            />
                          </div>
                        </div>

                        {/* <div class="row mb-3">
                            <label
                              for="about"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              About
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <textarea
                                name="about"
                                class="form-control"
                                id="about"
                                style={{ height: "100px" }}
                                defaultValue={admin_about}
                                onChange={(e) => setadmin_about(e.target.value)}
                              />
                            </div>
                          </div> */}

                        <div class="row mb-3">
                          <label
                            for="company"
                            class="col-md-4 col-lg-3 col-form-label"
                          >
                            Email
                          </label>
                          <div class="col-md-8 col-lg-9">
                            <input
                              name="company"
                              type="text"
                              class="form-control"
                              id="company"
                              defaultValue={user_email}
                              onChange={(e) => setuser_email(e.target.value)}
                            />
                          </div>
                        </div>

                        <div class="row mb-3">
                          <label
                            for="Job"
                            class="col-md-4 col-lg-3 col-form-label"
                          >
                            Contact
                          </label>
                          <div class="col-md-8 col-lg-9">
                            <input
                              name="job"
                              type="text"
                              class="form-control"
                              id="Job"
                              defaultValue={phone1}
                              onChange={(e) => setphone1(e.target.value)}
                            />
                          </div>
                        </div>

                        <div class="row mb-3">
                          <label
                            for="Country"
                            class="col-md-4 col-lg-3 col-form-label"
                          >
                            State
                          </label>
                          <div class="col-md-8 col-lg-9">
                            <input
                              name="country"
                              type="text"
                              class="form-control"
                              id="Country"
                              defaultValue={buyer_address}
                              onChange={(e) => setbuyer_address(e.target.value)}
                            />
                          </div>
                        </div>

                        <div class="text-center">
                          <button
                            type="submit"
                            class="btn btn-primary"
                            onClick={submitbtn}
                          >
                            Save Changes
                          </button>
                        </div>
                      </form>
                    </div>
                  </>

                  <div class="tab-pane fade profile-edit pt-3" id="wins"></div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </main>
  );
}
