import React, { useEffect, useState } from "react";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";

export default function ProductBuy() {
  const [product_name, setproduct_name] = useState("");
  const [description, setdescription] = useState("");
  const [seller_id, setseller_id] = useState("");
  const [starting_bid, setstarting_bid] = useState("");
  const [deadline, setdeadline] = useState("");

  const location = useLocation();
  const navigate = useNavigate();
  const id = location.pathname.split("/")[2]
    ? location.pathname.split("/")[2]
    : "";
  useEffect(() => {
    console.log(id);
    if (id) {
      getproduct();
    }
  }, []);

  const getproduct = async () => {
    const url = "http://localhost:1122/rfa/product/" + id;
    console.log(url);
    const res = await axios.get(url);
    console.log(res.data);
    setproduct_name(res.data.product_name);
    setdescription(res.data.description);
    setseller_id(res.data.seller_id);
    setstarting_bid(res.data.starting_bid);
    setdeadline(res.data.deadline);
  };
  return (
    <div class="main-wrapper" style={{ marginTop: "10px" }}>
      <div class="container">
        <div class="product-div">
          <div class="product-div-left">
            <div class="img-container">
              <img src="images/w1.png" alt="watch" />
            </div>
            {/* <div class="hover-container">
              <div>
                <img src="images/w1.png" />
              </div>
              <div>
                <img src="images/w2.png" />
              </div>
              <div>
                <img src="images/w3.png" />
              </div>
              <div>
                <img src="images/w4.png" />
              </div>
              <div>
                <img src="images/w5.png" />
              </div>
            </div> */}
          </div>
          <div class="product-div-right">
            <span class="product-name">(New) Analog Watch - For Men</span>
            <span class="product-price">$ 50.25</span>
            <div class="product-rating">
              <span>
                <i class="fas fa-star"></i>
              </span>
              <span>
                <i class="fas fa-star"></i>
              </span>
              <span>
                <i class="fas fa-star"></i>
              </span>
              <span>
                <i class="fas fa-star"></i>
              </span>
              <span>
                <i class="fas fa-star-half-alt"></i>
              </span>
              <span>(350 ratings)</span>
            </div>
            <p class="product-description">
              Lorem ipsum dolor, sit amet consectetur adipisicing elit. Vitae
              animi ad minima veritatis dolore. Architecto facere dignissimos
              voluptate fugit ratione molestias quis quidem exercitationem
              voluptas.
            </p>
            <div class="btn-groups">
              <button type="button" class="add-cart-btn">
                <i class="fas fa-shopping-cart"></i>add to cart
              </button>
              <button type="button" class="buy-now-btn">
                <i class="fas fa-wallet"></i>buy now
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
