import axios from "axios";
import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

export default function Profile() {
  const [user_name, setuser_name] = useState("");
  const [user_email, setuser_email] = useState("");
  const [phone1, setphone1] = useState("");
  const [password, setpassword] = useState("");
  const [buyer_address, setbuyer_address] = useState("");
  const [seller_name, setseller_name] = useState("");
  const [seller_email, setseller_email] = useState("");
  //   const [phone1, setphone1] = useState("");0
  //   const [password, setpassword] = useState("");
  const [seller_address, setseller_address] = useState("");
  const [img, setimg] = useState("");

  const [id, setId] = useState(sessionStorage.getItem("user"));
  const [role_id, setRole_Id] = useState(sessionStorage.getItem("role"));
  const [formErros, setFormErrros] = useState({});
  useEffect(() => {
    fatchUserName();
    console.log(id);
  }, [id]);

  const fatchUserName = async () => {
    if (role_id == 1) {
      try {
        const res = await axios.get("http://localhost:1122/rfa/user/" + id);
        console.log(res.data);
        setuser_name(res.data.user_name);
        setuser_email(res.data.user_email);
        setphone1(res.data.phone1);
        setpassword(res.data.password);
      } catch (error) {}
    }
    // } else if (role_id == 2) {
    //   try {
    //     const res = await axios.get("http://localhost:1122/rfa/sellers/" + id);
    //     console.log(res.data);
    //     setseller_name(res.data.seller_name);
    //     setseller_email(res.data.seller_email);
    //     setphone1(res.data.phone1);
    //     setpassword(res.data.password);
    //     setseller_address(res.data.seller_address);
    //   } catch (error) {}
    // }
  };

  const submitbtn = async (e) => {
    e.preventDefault();

    if (role_id == 1) {
      try {
        const data = {
          user_name,
          user_email,
          phone1,
          password,
          img,
        };
        let res = "";
        if (id) {
          res = await axios.put("http://localhost:1122/rfa/user/" + id, data);
        }
        alert(res.data);
        console.log(res.data);
        window.location.reload();
      } catch (error) {}
    }
    //    else if (role_id == 2) {
    //     try {
    //       const data = {
    //         seller_name,
    //         seller_email,
    //         phone1,
    //         password,
    //         seller_address,
    //         img,
    //       };
    //       let res = "";
    //       if (id) {
    //         res = await axios.put(
    //           "http://localhost:1122/rfa/sellers/" + id,
    //           data
    //         );
    //       }
    //       alert(res.data);
    //       window.location.reload();
    //     } catch (error) {}
    //   }
  };
  const [winner, setwinner] = useState([]);
  let i = 1;
  useEffect(() => {
    getwinner();
  }, []);
  const getwinner = async () => {
    const res = await axios.get("http://localhost:1122/rfa/winner/" + id);
    setwinner(res.data);
    console.log(res.data);
  };

  return (
    <main id="main" class="main">
      <section class="section profile" style={{ marginTop: "3%" }}>
        <div class="row">
          <div class="col-xl-4">
            <div class="card">
              <div class="card-body profile-card pt-4 d-flex flex-column align-items-center">
                <img
                  src={`http://localhost:1122/uploads/${img}`}
                  height={"500px"}
                  width={"500px"}
                  class="img-fluid"
                  alt="..."
                ></img>
                {role_id == 1 ? (
                  <>
                    <h2>{user_name}</h2>
                  </>
                ) : role_id == 2 ? (
                  <>
                    <h2>{seller_name}</h2>
                  </>
                ) : (
                  <>abcd</>
                )}
                {/* <h3>{job}</h3> */}
                <div class="social-links mt-2">
                  <a href="#" class="twitter">
                    <i class="bi bi-twitter"></i>
                  </a>
                  <a href="#" class="facebook">
                    <i class="bi bi-facebook"></i>
                  </a>
                  <a href="#" class="instagram">
                    <i class="bi bi-instagram"></i>
                  </a>
                  <a href="#" class="linkedin">
                    <i class="bi bi-linkedin"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>

          <div class="col-xl-8">
            <div class="card">
              <div class="card-body pt-3">
                <ul class="nav nav-tabs nav-tabs-bordered">
                  <li class="nav-item">
                    <button
                      class="nav-link active"
                      data-bs-toggle="tab"
                      data-bs-target="#profile-overview"
                    >
                      Overview
                    </button>
                  </li>

                  <li class="nav-item">
                    <button
                      class="nav-link"
                      data-bs-toggle="tab"
                      data-bs-target="#profile-edit"
                    >
                      Edit Profile
                    </button>
                  </li>

                  <li class="nav-item">
                    <button
                      class="nav-link"
                      data-bs-toggle="tab"
                      data-bs-target="#wins"
                    >
                      My Winning bids
                    </button>
                  </li>
                </ul>
                <div class="tab-content pt-2">
                  <div
                    class="tab-pane fade show active profile-overview"
                    id="profile-overview"
                  >
                    {role_id == 1 ? (
                      <>
                        <h5 class="card-title">About</h5>
                        <p class="small fst-italic">{}</p>

                        <h5 class="card-title">Profile Details</h5>
                        <div class="row">
                          <div class="col-lg-3 col-md-4 label ">Full Name</div>
                          <div class="col-lg-9 col-md-8">{user_name}</div>
                        </div>

                        <div class="row">
                          <div class="col-lg-3 col-md-4 label">Phone no.</div>
                          <div class="col-lg-9 col-md-8">{phone1}</div>
                        </div>

                        <div class="row">
                          <div class="col-lg-3 col-md-4 label">Email</div>
                          <div class="col-lg-9 col-md-8">{user_email}</div>
                        </div>

                        <div class="row">
                          <div class="col-lg-3 col-md-4 label">Address</div>
                          <div class="col-lg-9 col-md-8">{buyer_address}</div>
                        </div>
                      </>
                    ) : role_id == 2 ? (
                      <>
                        <h5 class="card-title">About</h5>
                        <p class="small fst-italic">{}</p>

                        <h5 class="card-title">Profile Details</h5>
                        <div class="row">
                          <div class="col-lg-3 col-md-4 label ">Full Name</div>
                          <div class="col-lg-9 col-md-8">{seller_name}</div>
                        </div>

                        <div class="row">
                          <div class="col-lg-3 col-md-4 label">Email</div>
                          <div class="col-lg-9 col-md-8">{seller_email}</div>
                        </div>

                        <div class="row">
                          <div class="col-lg-3 col-md-4 label">Phone</div>
                          <div class="col-lg-9 col-md-8">{phone1}</div>
                        </div>

                        <div class="row">
                          <div class="col-lg-3 col-md-4 label">Address</div>
                          <div class="col-lg-9 col-md-8">{seller_address}</div>
                        </div>
                      </>
                    ) : (
                      <></>
                    )}
                  </div>
                  {role_id == 1 ? (
                    <>
                      <div
                        class="tab-pane fade profile-edit pt-3"
                        id="profile-edit"
                      >
                        <form>
                          <div class="row mb-3">
                            <label
                              for="profileImage"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              Profile Image
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <img
                                src="assets/img/profile-img.jpg"
                                alt="Profile"
                              />
                              {/* <div class="pt-2">
                                <a
                                  href="#"
                                  class="btn btn-primary btn-sm"
                                  title="Upload new profile image"
                                >
                                  <i class="bi bi-upload"></i>
                                </a>
                                <a
                                  href="#"
                                  class="btn btn-danger btn-sm"
                                  title="Remove my profile image"
                                >
                                  <i class="bi bi-trash"></i>
                                </a>
                              </div> */}
                              {/* <div class="col-md-6">
                                <div class="form-floating">
                                  <input
                                    type="file"
                                    class="form-control"
                                    placeholder="Profile Image"
                                    defaultValue={img}
                                    onChange={(e) =>
                                      setdescription(e.target.files[0])
                                    }
                                  />
                                  <label for="floatingPassword">
                                    Description
                                  </label>
                                </div>
                              </div> */}
                            </div>
                          </div>

                          <div class="row mb-3">
                            <label
                              for="fullName"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              Full Name
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <input
                                name="fullName"
                                type="text"
                                class="form-control"
                                id="fullName"
                                defaultValue={user_name}
                                onChange={(e) => setuser_name(e.target.value)}
                              />
                            </div>
                          </div>

                          {/* <div class="row mb-3">
                            <label
                              for="about"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              About
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <textarea
                                name="about"
                                class="form-control"
                                id="about"
                                style={{ height: "100px" }}
                                defaultValue={admin_about}
                                onChange={(e) => setadmin_about(e.target.value)}
                              />
                            </div>
                          </div> */}

                          <div class="row mb-3">
                            <label
                              for="company"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              Email
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <input
                                name="company"
                                type="text"
                                class="form-control"
                                id="company"
                                defaultValue={user_email}
                                onChange={(e) => setuser_email(e.target.value)}
                              />
                            </div>
                          </div>

                          <div class="row mb-3">
                            <label
                              for="Job"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              Contact
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <input
                                name="job"
                                type="text"
                                class="form-control"
                                id="Job"
                                defaultValue={phone1}
                                onChange={(e) => setphone1(e.target.value)}
                              />
                            </div>
                          </div>

                          <div class="row mb-3">
                            <label
                              for="Country"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              State
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <input
                                name="country"
                                type="text"
                                class="form-control"
                                id="Country"
                                defaultValue={buyer_address}
                                onChange={(e) =>
                                  setbuyer_address(e.target.value)
                                }
                              />
                            </div>
                          </div>

                          <div class="text-center">
                            <button
                              type="submit"
                              class="btn btn-primary"
                              onClick={submitbtn}
                            >
                              Save Changes
                            </button>
                          </div>
                        </form>
                      </div>
                    </>
                  ) : role_id == 2 ? (
                    <>
                      <div
                        class="tab-pane fade profile-edit pt-3"
                        id="profile-edit"
                      >
                        <form>
                          <div class="row mb-3">
                            <label
                              for="profileImage"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              Profile Image
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <img
                                src="assets/img/profile-img.jpg"
                                alt="Profile"
                              />
                              <div class="pt-2">
                                <a
                                  href="#"
                                  class="btn btn-primary btn-sm"
                                  title="Upload new profile image"
                                >
                                  <i class="bi bi-upload"></i>
                                </a>
                                <a
                                  href="#"
                                  class="btn btn-danger btn-sm"
                                  title="Remove my profile image"
                                >
                                  <i class="bi bi-trash"></i>
                                </a>
                              </div>
                            </div>
                          </div>

                          <div class="row mb-3">
                            <label
                              for="fullName"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              Full Name
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <input
                                name="fullName"
                                type="text"
                                class="form-control"
                                defaultValue={seller_name}
                                onChange={(e) => setseller_name(e.target.value)}
                              />
                            </div>
                          </div>

                          {/* <div class="row mb-3">
                            <label
                              for="about"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              About
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <textarea
                                name="about"
                                class="form-control"
                                style={{ height: "100px" }}
                                defaultValue={about}
                                onChange={(e) => setAbout(e.target.value)}
                              />
                            </div>
                          </div> */}

                          <div class="row mb-3">
                            <label
                              for="Job"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              First Email
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <input
                                name="Email"
                                type="Email"
                                class="form-control"
                                id="fullName"
                                defaultValue={seller_email}
                                onChange={(e) =>
                                  setseller_email(e.target.value)
                                }
                              />
                            </div>
                          </div>

                          <div class="row mb-3">
                            <label
                              for="Country"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              Address
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <input
                                name="country"
                                type="text"
                                class="form-control"
                                defaultValue={seller_address}
                                onChange={(e) =>
                                  setseller_address(e.target.value)
                                }
                              />
                            </div>
                          </div>

                          <div class="row mb-3">
                            <label
                              for="Address"
                              class="col-md-4 col-lg-3 col-form-label"
                            >
                              Your Subject
                            </label>
                            <div class="col-md-8 col-lg-9">
                              <input
                                name="address"
                                type="text"
                                class="form-control"
                                defaultValue={phone1}
                                onChange={(e) => setphone1(e.target.value)}
                              />
                            </div>
                          </div>

                          <div class="text-center">
                            <button
                              type="submit"
                              class="btn btn-primary"
                              onClick={submitbtn}
                            >
                              Save Changes
                            </button>
                          </div>
                        </form>
                      </div>
                    </>
                  ) : (
                    <></>
                  )}
                  <div class="tab-pane fade profile-edit pt-3" id="wins"></div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </main>
  );
}
